class BtchartType extends BaseBtchartType
{
    public function getChartTypeDetails($chart_type)
    {
   		$q = Doctrine_Query::create()                          
            ->from('BtchartType bc')
            ->where('bc.id = ?', $chart_type)
            ->fetchOne(); 
        return $q;   
    }
    public function getChartTypes()
    {
   		$q = Doctrine_Query::create()
            //->select('bc.id','bc.chart_type_name')                          
            ->from('BtchartType bc')
            ->execute(); 
        $list = array();
        foreach($q as $data){
            $list[$data->id] = $data->chart_type_name;
        }
        return $list;   
        
    }
    public function getChartFileName($chart_type)
    {
   		$q = Doctrine_Query::create()
            ->select('bc.chart_type_image')                          
            ->from('BtchartType bc')
            ->where('bc.id = ?', $chart_type)
            ->fetchOne(); 
        return $q->chart_type_image;          
    }
    public function getPaidChartTypes($addAll=false)
    {
   		$q = Doctrine_Query::create()
            ->select('bc.id','bc.chart_type_name')                          
            ->from('BtchartType bc')
            ->where('bc.chart_type_category = ?','paid')
            ->execute(); 
        $list = array();
        foreach($q as $data){
            $list[$data->id] = $data->chart_type_name;
        }
        if($addAll){
            $list[0]='Alla';
        }
        return $list;   
        
    }
    public function getChartTypeTextFromId($chart_type_id)
    {
   		$q = Doctrine_Query::create()
            ->select('bc.chart_type_name')                          
            ->from('BtchartType bc')
            ->where('bc.id = ?', $chart_type_id)
            ->fetchOne(); 
        return $q->chart_type_name;                  
    }    
}