class SbtFavorite extends BaseSbtFavorite
{
   /**
	*
	* This function add item to you favorite list.
	*
	*/
	public function addToFavorite($arr,$item_name) 
	{ 
		if($arr[0] && $arr[1] && $arr[2])
		{
			$new_rec = new SbtFavorite();
			$new_rec->f_type = $arr[0];
			$new_rec->f_id = $arr[1];
			$new_rec->user_id = $arr[2];
			$new_rec->f_name = $item_name;
			$new_rec->f_notification = $arr[3] ? $arr[3] : 0;
			$new_rec->created_at = date("Y-m-d H:i:s");
			$new_rec->save();
		}
	}
	
   /**
	*
	* This function returns specified favorite lists.
	*
	*/
	public function getSpecifiedFavorite($type,$user_id,$column_id,$order) 
	{
		$query = Doctrine_Query::create()->from('SbtFavorite sf')->where('sf.f_type = ? AND sf.user_id = ?', array($type,$user_id));
                if($column_id){
                    switch ($column_id) {
                        case 'article': $query = $query->orderBy('sf.f_name ' . $order);
                            break;
                        case 'default': $query = $query->orderBy('sf.created_at DESC');
                            break;
                    }
                }
                if (!$column_id)
                    $query = $query->orderBy('sf.created_at DESC');
		return $query;
	}
	
   /**
	*
	* This function returns specified record of user.
	*
	*/
	public function isAddedInFavoriteList($type,$art_id,$user_id) 
	{ 
		$query = Doctrine_Query::create()->from('SbtFavorite sf')->where('sf.f_type = ? AND sf.user_id = ? AND sf.f_id = ?', array($type,$user_id,$art_id));
		$data = $query->fetchOne();
		return $data;
	}
	
   /**
	*
	* This function returns specified favorite record.
	*
	*/
	public function getSingleFavoriteRecord($id) 
	{ 
		$query = Doctrine_Query::create()->from('SbtFavorite sf')->where('sf.id = ? ', $id);
		$data = $query->fetchOne();
		return $data;
	}
	
   /**
	*
	* This function returns list of users who have requested for notification on update on perticular article, blog, forum.
	*
	*/
	public function getFavNotificationUserList($type,$art_id) 
	{ 
		$query = Doctrine_Query::create()->select('sf.user_id as user_id')->from('SbtFavorite sf')->where('sf.f_type = ? AND sf.f_id = ? AND sf.f_notification = ?', array($type,$art_id,1));
		$data = $query->fetchArray();
		return $data;
	}
	
   /**
	*
	* This function returns a status of weither the user has notification.
	*
	*/
	public function getFavNotificationStatus($arr) 
	{ 
		$query = Doctrine_Query::create()->from('SbtFavorite sf')->where('sf.f_type = ? AND sf.f_id = ? AND sf.user_id = ?', array($arr[0],$arr[1],$arr[2]));
		$data = $query->fetchOne();
		if($data) return $data->f_notification;
		else return 2;
	}
	
   /**
	*
	* This function updates notification status.
	*
	*/
	public function updateFavNotificationStatus($arr,$status) 
	{ 
		$query = Doctrine_Query::create()->from('SbtFavorite sf')->where('sf.f_type = ? AND sf.f_id = ? AND sf.user_id = ?', array($arr[0],$arr[1],$arr[2]));
		$data = $query->fetchOne();
		if($data)
		{ 
			$data->f_notification = $status;
			$data->save();
		}
	}
	
}